import React from 'react';
import { motion } from 'framer-motion';

export default function Hero() {
  return (
    <section className='min-h-screen flex flex-col justify-center items-center text-center px-6'>
      <motion.h1 initial={{opacity:0, y:-50}} animate={{opacity:1, y:0}} transition={{duration:1}} className='text-6xl font-bold mb-4'>
        Hi, I'm <span className='text-yellow-300'>Sourav</span>
      </motion.h1>
      <motion.p initial={{opacity:0}} animate={{opacity:1}} transition={{delay:0.5}} className='text-xl max-w-2xl'>
        A passionate Front-End Developer crafting premium user experiences.
      </motion.p>
    </section>
  );
}
