import React from 'react';
import { motion } from 'framer-motion';

export default function Projects() {
  const sampleProjects = [
    {title:'Project One', desc:'A cool project description.'},
    {title:'Project Two', desc:'Another awesome project.'},
    {title:'Project Three', desc:'More description for your work.'}
  ];

  return (
    <section id='projects' className='py-20 px-6 bg-marvelRed/80'>
      <h2 className='text-4xl font-bold text-center mb-12'>Projects</h2>
      <div className='grid md:grid-cols-3 gap-8'>
        {sampleProjects.map((proj,i)=>(
          <motion.div key={i} whileHover={{scale:1.05}} className='bg-darkRed rounded-2xl p-6 shadow-lg'>
            <h3 className='text-2xl font-semibold mb-2'>{proj.title}</h3>
            <p className='text-gray-300'>{proj.desc}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
