import React from "react";
import { motion } from "framer-motion";
import { ExternalLink } from "lucide-react";

const frontendProjects = [
  {
    title: "Coming Soon Page",
    desc: "A simple 'Coming Soon' page with subscription form made in HTML/CSS.",
    screenshot: "/coming-soon.png",
    liveLink: "#", // Replace with live link if hosted
    codeLink: "https://github.com/souravgour-17/frontend-coming-soon"
  },
  {
    title: "Emoji Flip Game",
    desc: "A fun memory card game built with HTML, CSS & JS where players flip cards to match emojis.",
    screenshot: "/emoji-flip.png", // Add this image to your public/ folder
    liveLink: "https://souravgour-17.github.io/Emoji_flip_game/",
    codeLink: "https://github.com/souravgour-17/Emoji_flip_game"
  },
  {
    title: "Number Guess Game",
    desc: "An interactive guessing game where players try to guess the correct number with hints.",
    screenshot: "/number-guess.png", // Add screenshot in public/
    liveLink: "https://souravgour-17.github.io/Number-Guess/",
    codeLink: "https://github.com/souravgour-17/Number-Guess"
  },
  {
    title: "Gradient Calculator",
    desc: "A stylish calculator with gradient design built using HTML, CSS & JavaScript.",
    screenshot: "/calculator.png", // Add screenshot in public/
    liveLink: "https://souravgour-17.github.io/calculator/",
    codeLink: "https://github.com/souravgour-17/calculator"
  }
];

export default function FrontendProjects() {
  return (
    <section
      id="frontend-projects"
      className="py-20 px-6 bg-gray-50 dark:bg-neutral-900"
    >
      <h2 className="text-4xl font-bold text-center mb-12">Frontend Designs</h2>
      <div className="grid md:grid-cols-3 gap-8">
        {frontendProjects.map((proj, i) => (
          <motion.div
            key={i}
            whileHover={{ scale: 1.05 }}
            className="bg-white dark:bg-neutral-800 p-6 rounded-2xl shadow-lg flex flex-col justify-between"
          >
            <img
              src={proj.screenshot}
              alt={proj.title}
              className="rounded-xl mb-4 object-cover h-48 w-full"
            />
            <div>
              <h3 className="text-2xl font-semibold mb-2">{proj.title}</h3>
              <p className="text-gray-700 dark:text-gray-300 mb-4">{proj.desc}</p>
            </div>
            <div className="flex justify-between mt-auto">
              <a
                href={proj.codeLink}
                target="_blank"
                rel="noreferrer"
                className="text-indigo-500 hover:underline flex items-center"
              >
                Code <ExternalLink className="ml-1 w-4 h-4" />
              </a>
              {proj.liveLink && (
                <a
                  href={proj.liveLink}
                  target="_blank"
                  rel="noreferrer"
                  className="text-green-500 hover:underline flex items-center"
                >
                  Live <ExternalLink className="ml-1 w-4 h-4" />
                </a>
              )}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
