import React from "react";
import { motion } from "framer-motion";
import Typical from "react-typical"; // install via npm i react-typical

export default function Hero() {
  return (
    <section className="min-h-screen flex flex-col justify-center items-center text-center px-6">
      <motion.h1
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="text-6xl font-bold mb-4"
      >
        Hi, I'm <span className="text-yellow-300">Sourav</span>
      </motion.h1>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="text-xl max-w-2xl mb-6"
      >
        I'm a{" "}
        <Typical
          steps={["Front-End Developer", 1500, "React Enthusiast", 1500, "UI/UX Designer", 1500]}
          loop={Infinity}
          wrapper="span"
        />
      </motion.p>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1 }}
        className="flex gap-4"
      >
        <a href="#projects" className="px-6 py-3 bg-indigo-500 text-white rounded-xl hover:bg-indigo-600 transition">
          View Projects
        </a>
        <a href="#contact" className="px-6 py-3 border border-indigo-500 text-indigo-500 rounded-xl hover:bg-indigo-500 hover:text-white transition">
          Hire Me
        </a>
      </motion.div>
    </section>
  );
}
