import React from "react";
import { motion } from "framer-motion";
import { ExternalLink } from "lucide-react";
import comingSoonGif from "../../assets/original-0d5513e170e64ae46730b0205f0b883a.gif";
const frontendProjects = [
  {
    title: "Coming Soon Page",
    desc: "A simple 'Coming Soon' page with subscription form made in HTML/CSS.",
    screenshot: "/images/coming-soon.png", // Place this image in public/images/
    liveLink: "#", // Optional: replace with hosted page URL
    codeLink: "https://github.com/souravgour-17/frontend-coming-soon"
  },
  // You can add more frontend projects here following the same structure
];

export default function FrontendProjects() {
  return (
    <section id="frontend-projects" className="py-20 px-6 bg-gray-50 dark:bg-neutral-900">
      <h2 className="text-4xl font-bold text-center mb-12">Frontend Designs</h2>
      <div className="grid md:grid-cols-3 gap-8">
        {frontendProjects.map((proj, i) => (
          <motion.div
            key={i}
            whileHover={{ scale: 1.05 }}
            className="bg-white dark:bg-neutral-800 p-6 rounded-2xl shadow-lg flex flex-col justify-between"
          >
            <img
              src={proj.screenshot}
              alt={proj.title}
              className="rounded-xl mb-4 object-cover h-48 w-full"
            />
            <div>
              <h3 className="text-2xl font-semibold mb-2">{proj.title}</h3>
              <p className="text-gray-700 dark:text-gray-300 mb-4">{proj.desc}</p>
            </div>
            <div className="flex justify-between mt-auto">
              <a
                href={proj.codeLink}
                target="_blank"
                rel="noreferrer"
                className="text-indigo-500 hover:underline flex items-center"
              >
                Code <ExternalLink className="ml-1 w-4 h-4" />
              </a>
              {proj.liveLink && (
                <a
                  href={proj.liveLink}
                  target="_blank"
                  rel="noreferrer"
                  className="text-green-500 hover:underline flex items-center"
                >
                  Live <ExternalLink className="ml-1 w-4 h-4" />
                </a>
              )}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
